/* eslint-disable no-console */
/* eslint-disable react/jsx-props-no-spreading */
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import {
  AppBar, Button, Container, Toolbar, Typography, Stack, Box, Avatar, IconButton, TextField,
} from '@mui/material';
import MyPost from '../components/MyPost';
import OtherPost from '../components/OtherPost';
import { getGroupById, changeGroup, deleteGroupById } from '../api/groups';

export default function EditPost({ userId }) {
  if (userId === null) {
    return <div>Loading...</div>;
  }

  const { groupId } = useParams();

  const [time, setTime] = useState('');
  const [date, setDate] = useState('');
  const [destination, setDestination] = useState('');
  const [departure, setDeparture] = useState('');
  const [transportation, setTransportation] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  const handleEditPost = (e) => {
    console.log('handle edit post');
  };

  return (
    <div>
      <Typography
        variant="h5"
        component="div"
        sx={{
          flexGrow: 1, fontWeight: 800, fontSize: '2.5rem',
        }}
        color="secondary"
        align="center"
      >
        Edit Post
      </Typography>
      <form onSubmit={handleSubmit}>
        <div style={{
          display: 'flex',
          flexDirection: 'column',
          border: '1px solid #ccc',
          padding: '16px',
          borderRadius: '8px',
          alignItems: 'center',
        }}
        >
          <TextField
            label="Time"
            variant="filled"
            required
            value={time}
            onChange={(e) => setTime(e.target.value)}
            data-testid="Time"
            style={{ marginBottom: '16px', width: '200px' }}
          />
          <TextField
            label="Date"
            variant="filled"
            required
            value={date}
            onChange={(e) => setDate(e.target.value)}
            data-testid="Date"
            style={{ marginBottom: '16px', width: '200px' }}
          />
          <TextField
            label="Destination"
            variant="filled"
            required
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
            data-testid="Destination"
            style={{ marginBottom: '16px', width: '400px' }}
          />
          <TextField
            label="Departure From"
            variant="filled"
            required
            value={departure}
            onChange={(e) => setDeparture(e.target.value)}
            data-testid="Departure"
            style={{ marginBottom: '16px', width: '400px' }}
          />
          <TextField
            label="Transportation Method"
            variant="filled"
            required
            value={transportation}
            onChange={(e) => setTransportation(e.target.value)}
            style={{ marginBottom: '16px', width: '400px' }}
          />
          <Button
            variant="contained"
            color="secondary"
            style={{ backgroundColor: '#EA3C3C', color: 'white', width: '200px' }}
            type="submit"
            onClick={handleEditPost}
          >
            Post
          </Button>
        </div>
      </form>
    </div>
  );
}
